import 'package:flutter/material.dart';import 'package:provider/provider.dart';import 'medicine_store.dart';import 'screens/initial_screen.dart';void main() {  runApp(    ChangeNotifierProvider(      create: (_) => MedicineStore()..loadItems(),      child: const MyApp(),    ),  );}class MyApp extends StatefulWidget {  const MyApp({super.key});  @override  State<MyApp> createState() => _MyAppState();}class _MyAppState extends State<MyApp> {  @override  Widget build(BuildContext context) {    return MaterialApp(      title: 'My Medicines',      theme: ThemeData(        colorScheme: ColorScheme.fromSeed(seedColor: Colors.blue),        useMaterial3: true,      ),      home: const InitialScreen(),    );  }}